function Articles({ posts }) {
  return (
    <div>
      {
        posts.data.map((article,i) => (
        <div key={article.id}>
             <Link href="/article/${article.attributes.id}">
                <a>{article.attributes.title}</a>
            </Link>
          <div
              dangerouslySetInnerHTML={{
              __html: article.attributes.body.processed
          }}>
          </div>

        </div>
        ))
      }
    </div>
  )
}



export async function getStaticProps() {
  // Call an external API endpoint to get posts
  const res = await fetch('http://webserver/jsonapi/node/article')
  const posts = await res.json()
  console.log('inside Articles...');
  // By returning { props: posts }, the Blog component
  // will receive `posts` as a prop at build time
  return {
    props: {
      posts
    },
    revalidate:10
  }
}

export default Articles
