function Article({ post }) {
    //console.log(post)

    return (
      <div>
        <div>
          <h2>{post.data.attributes.title}</h2>
          <p>{post.data.attributes.body}</p>
        </div>
      </div>
    )
  }

  export async function getStaticPaths() {
    const res = await fetch('http://webserver/jsonapi/node/article')
    const {data} = await res.json()
    const paths = data.map((article) => `/article/${article.id}`)
    return { paths, fallback: false }
  }

  export async function getStaticProps({ params }) {
    const res = await fetch(`http://webserver/jsonapi/node/article/${params.id}`)
    const post = await res.json()
    return { props: { post } }
  }



  export default Article
